async function loadAllInstances(token) {
  if(token.tokenInstance === undefined || token.stakingInstance === undefined || config_json.dim.dimInstance === undefined ||  token.decimals === 0) {
    token.tokenInstance = new web3.eth.Contract(token.tokenABI, token.tokenAddress)
    config_json.dim.dimInstance = new web3.eth.Contract(config_json.dim.dimABI, config_json.dim.dimAddress)
    token.stakingInstance = new web3.eth.Contract(token.stakingABI, token.stakingAddress)
    const decimals = await token.tokenInstance.methods.decimals().call()
    token.decimals = Math.pow(10, decimals)
    return
  } else {
    return
  }
}

//rounding functions
function parseFloat(str, radix) {
  let parts = str.split(".");
  if ( parts.length > 1 ) {
    return parseInt(parts[0], radix) + parseInt(parts[1], radix) / Math.pow(radix, parts[1].length);
  }
  return parseInt(parts[0], radix);
}


function roundToFour(num) {
  return +(Math.floor(num + 'e+4') + 'e-4')
}

function roundToTwo(num) {
  return +(Math.floor(num + 'e+2') + 'e-2')
}

function round(num) {
  return +(Math.floor(num + 'e+0') + 'e-0')
}

function roundToTwoOrFour(num){
  if(num<1) return roundToFour(num)
  return roundToTwo(num)
}

