async function updateStakedBalance(pool, pool_number) {
  await loadAllInstances(pool)
  const stakedBalance = await pool.stakingInstance.methods.balanceOf(myaddress).call()
  document.getElementById('stakedBalance'+pool_number).innerHTML = roundToTwoOrFour(stakedBalance/pool.decimals)
}

async function updateRewards(pool, pool_number) {
  await loadAllInstances(pool)
  const rewards = await pool.stakingInstance.methods.rewards(myaddress).call()
  document.getElementById('rewards'+pool_number).innerHTML = roundToTwoOrFour(rewards/pool.decimals)
}

async function updateApyAndTotal(pool, pool_number) {
  await loadAllInstances(pool)
  const rewardRate = await pool.stakingInstance.methods.rewardRate().call()
  const totalSupply = await pool.stakingInstance.methods.totalSupply().call()
  document.getElementById('apy'+pool_number).innerHTML = roundToTwoOrFour(totalSupply/(rewardRate*60*60*24))+'%'
  document.getElementById('totalStaked'+pool_number).innerHTML = roundToTwoOrFour(totalSupply/pool.decimals)
}


async function checkAllowance(pool, pool_number) {
  await loadAllInstances(pool)
  pool.allowance = await pool.tokenInstance.methods.allowance(myaddress, pool.stakingAddress).call()
  if(pool.allowance>=pool.lastVal*pool.decimals) {
    document.getElementById('approve_or_stake'+pool_number).innerHTML = 'Stake'
    pool.approve_or_stake = 0
  } else {
    document.getElementById('approve_or_stake'+pool_number).innerHTML = 'Approve'
    pool.approve_or_stake = 1
  }
  return
}

async function stake(pool_number) {
  const pool = config_json.tokens["token"+pool_number]
  await loadAllInstances(pool)
  let amount = document.getElementById('tokenAmount'+pool_number).value
  pool.lastVal = amount
  amount = web3.utils.toBN((amount*pool.decimals).toLocaleString('fullwide', {useGrouping:false}))
  await checkAllowance(pool, pool_number)
  if(pool.approve_or_stake === 0){
    const gasPrice = await web3.eth.getGasPrice()
    let args = {
      from:myaddress,
      gasPrice:gasPrice,
      value: 0,
    }
    await pool.stakingInstance.methods.stake(amount).send(args)
  }
  else {
    await approve(pool, pool_number)
  }
}

async function approve(pool, pool_number) {
  await loadAllInstances(pool)
  const gasPrice = await web3.eth.getGasPrice()
  const amount = web3.utils.toBN((999999999999*pool.decimals).toLocaleString('fullwide', {useGrouping:false}))
  let args = {
    from:myaddress,
    gasPrice:gasPrice,
    value: 0,
  }
  await pool.tokenInstance.methods.approve(pool.stakingAddress, amount).send(args)
}

async function unstake(pool_number) {
  const pool = config_json.tokens["token"+pool_number]
  await loadAllInstances(pool)
  const gasPrice = await web3.eth.getGasPrice()
  let amount = document.getElementById('tokenAmount'+pool_number).value
  amount = web3.utils.toBN((amount*pool.decimals).toLocaleString('fullwide', {useGrouping:false}))
  let args = {
    from:myaddress,
    gasPrice:gasPrice,
    value: 0,
  }
  await pool.stakingInstance.methods.withdraw(amount).send(args)
}

async function harvest(pool_number) {
  const pool = config_json.tokens["token"+pool_number]
  await loadAllInstances(pool)
  const gasPrice = await web3.eth.getGasPrice()
  let args = {
    from:myaddress,
    gasPrice:gasPrice,
    value: 0,
  }
  await pool.stakingInstance.methods.getReward().send(args)
}

$( "#stake" ).click(async function() {
  stake()
})

$( "#unstake" ).click(function() {
  unstake()
})

$( "#harvest" ).click(function() {
  harvest()
})
